const toggle = interval(5000);
const example = source.pipe(
  // &#x6BCF;5&#x79D2;&#x5F00;&#x542F;&#x7A97;&#x53E3;
  windowToggle(toggle, val =&gt; interval(val * 1000)),
  tap(_ =&gt; console.log(&apos;NEW WINDOW!&apos;))
);

const subscribeTwo = example
  .pipe(
    // &#x7A97;&#x53E3;&#x53D1;&#x51FA;&#x5D4C;&#x5957;&#x7684; observable
