  return of(&apos;Request Complete!&apos;).pipe(delay(timeToDelay));
}

of(4000, 3000, 2000)
  .pipe(
    concatMap(duration =&gt;
      makeRequest(duration).pipe(
        timeout(2500),
        catchError(error =&gt; of(`Request timed out after: ${duration}`))
